@NgModule({
  bootstrap: [AppComponent],
  declarations: [
	AppComponent,
	LinearGaugeScaleComponent
],
  imports: [
	BrowserModule,
	BrowserAnimationsModule,
	FormsModule,
	IgxLinearGaugeModule
],
  providers: [],
  entryComponents: [],
  schemas: []
})
export class AppModule {}
