// added to fix SB layout:
.nav-bar {
    background: #09f;
    padding: 0.5rem;
    display: flex;
    flex-direction: row;
}

.nav-bar > div {
    // background: #c300ff;
    display: flex;
    flex-direction: row;
}

.nav-bar > nav {
    // background: #ff0000;
    display: flex;
    flex-direction: row;
}

.igx-navbar__left {
    // background: rgb(255, 136, 0);
    display: flex;
    flex-direction: row;
}

.igx-navbar__left > igx-icon {
    align-self: center;
    padding-right: 1rem;
}

.igx-navbar__left > h1 {
    margin-block-start: 0.3em;
    margin-block-end: 0.3em;
}

.nav-sidebar {
    // max-height: 500px;
    // height: 500px;
    overflow-y: auto;
    overflow-x: hidden;
}


// .sample {
